package com.logibeat.cloud.controller;import com.logibeat.cloud.common.annotation.NotLogin;import com.logibeat.cloud.common.vo.EntClassLineHistoryVo;import com.logibeat.cloud.services.EntClassLineService;import com.logibeat.cloud.util.tools.responseMdl.JSONPrompt;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Scope;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;/** * Created by Yujinjun on 2016/8/15. */@Controller@RequestMapping(value = "Driver/api/classLine")@Scope("prototype")public class EntClassLineController extends BaseController{    @Autowired    protected EntClassLineService entClassLineService;    /**     * 获得历史线路     *     * @return     */    @RequestMapping(value = "/getHistoryLine")    @ResponseBody    @NotLogin    public JSONPrompt<List<EntClassLineHistoryVo>> getHistoryLine(String entId) {        List<EntClassLineHistoryVo> list = entClassLineService.getHistoryLine(entId, baseUserId);        return new JSONPrompt(list);    }}