package com.logibeat.cloud.common.enumtype;/** * Created by Yujinjun on 2016/7/28. */public enum NameType {    Unknown(0, "未知（全部）"),    EnterpriseName(1, "企业名称"),    DriverName(2, "司机名称");    private Integer value;    private String description;    NameType(Integer value, String description) {        this.value = value;        this.description = description;    }    public Integer getValue() {        return value;    }    public String getDescription() {        return description;    }}