package com.logibeat.cloud.common.enumtype;/** * Created by Yujinjun on 2017/2/9. */public enum OauthType {    Unknown(0, "未知（全部）"),    Person(1, "个人"),    Ent(2, "企业");    private Integer value;    private String description;    OauthType(Integer value, String description) {        this.value = value;        this.description = description;    }    public Integer getValue() {        return value;    }    public String getDescription() {        return description;    }}