package com.logibeat.cloud.common.enumtype;/** * Created by Yujinjun on 2016/5/20. */public enum OrdersType {    Unknown(0, "未知（全部）"),    CreateRoute(1, "创建行程"),    CreateRouteNoEnt(2, "创建行程(没企业)"),        MUCK_GeneralOrder(31, "普通运输单(渣土应用)"),    MUCK_SelfOwnedOrder(32, "自运单(渣土应用)");    private Integer value;    private String description;    OrdersType(Integer value, String description) {        this.value = value;        this.description = description;    }    public Integer getValue() {        return value;    }    public String getDescription() {        return description;    }}