package com.logibeat.cloud.core.constant;/** * 加密工具类 * Created by Yujinjun on 2016/4/19. */public class EncryptUtil {    /**     * 获得别名的方法，如果没有entId，则写null     *     * @param entId     * @param baseUserId     * @param key     * @return     */    public static String getAlias(String entId, String baseUserId, String key) {        return TypeCastUtil.getMD5(TypeCastUtil.getMD5(entId + baseUserId) + key);    }}